import React, { useState } from "react";
import axios from "axios";

function App() {
  const [file, setFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const [editableData, setEditableData] = useState({});

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    // Create image preview
    if (selectedFile) {
      const reader = new FileReader();
      reader.onload = (e) => setImagePreview(e.target.result);
      reader.readAsDataURL(selectedFile);
    } else {
      setImagePreview(null);
    }
  };

  const handleUpload = async () => {
    if (!file) {
      setError("Please choose an image first.");
      return;
    }
    setError("");
    setLoading(true);
    setResult(null);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post(
        "https://76fbp6nkjlowmolrprocdec5qa0eryku.lambda-url.us-east-1.on.aws/describe",
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      setResult(res.data);
    } catch (err) {
      console.error(err);
      setError("Error fetching description. Check console for details.");
    } finally {
      setLoading(false);
    }
  };

  const containerStyle = {
    maxWidth: '800px',
    margin: '2rem auto',
    padding: '2rem',
    fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    minHeight: '100vh',
    color: '#333'
  };

  const cardStyle = {
    background: 'white',
    borderRadius: '16px',
    padding: '2rem',
    boxShadow: '0 10px 30px rgba(0,0,0,0.2)',
    marginBottom: '2rem'
  };

  const titleStyle = {
    textAlign: 'center',
    color: '#2c3e50',
    fontSize: '2.5rem',
    fontWeight: '600',
    marginBottom: '2rem',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '0.5rem'
  };

  const uploadAreaStyle = {
    border: '2px dashed #667eea',
    borderRadius: '12px',
    padding: '2rem',
    textAlign: 'center',
    background: '#f8f9ff',
    transition: 'all 0.3s ease',
    cursor: 'pointer',
    marginBottom: '1.5rem'
  };

  const fileInputStyle = {
    display: 'none'
  };

  const buttonStyle = {
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    padding: '0.75rem 2rem',
    fontSize: '1rem',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    boxShadow: '0 4px 15px rgba(102, 126, 234, 0.4)',
    width: '100%',
    marginTop: '1rem'
  };

  const disabledButtonStyle = {
    ...buttonStyle,
    background: '#ccc',
    cursor: 'not-allowed',
    boxShadow: 'none'
  };

  const imagePreviewStyle = {
    maxWidth: '300px',
    maxHeight: '200px',
    borderRadius: '8px',
    margin: '1rem auto',
    display: 'block',
    boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
  };

  const resultStyle = {
    marginTop: '2rem',
    textAlign: 'left',
    background: '#f8f9fa',
    padding: '1.5rem',
    borderRadius: '12px',
    border: '1px solid #e9ecef'
  };

  const errorStyle = {
    color: '#e74c3c',
    background: '#ffeaea',
    padding: '1rem',
    borderRadius: '8px',
    border: '1px solid #f5c6cb',
    marginTop: '1rem'
  };

  return (
    <div style={containerStyle}>
      <div style={cardStyle}>
        <h1 style={titleStyle}>
          <span>🎄</span>
          Holiday Product Describer
        </h1>

        <div style={uploadAreaStyle} onClick={() => document.getElementById('fileInput').click()}>
          <input 
            id="fileInput"
            type="file" 
            accept="image/*" 
            onChange={handleFileChange}
            style={fileInputStyle}
          />
          <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>📷</div>
          <p style={{ margin: 0, fontSize: '1.1rem', color: '#667eea' }}>
            {file ? file.name : 'Click to upload product image'}
          </p>
          <p style={{ margin: '0.5rem 0 0 0', fontSize: '0.9rem', color: '#666' }}>
            Supports PNG, JPG, JPEG formats
          </p>
        </div>

        {imagePreview && (
          <div style={{ textAlign: 'center', marginBottom: '1.5rem' }}>
            <h4 style={{ color: '#2c3e50', marginBottom: '1rem' }}>Preview:</h4>
            <img 
              src={imagePreview} 
              alt="Preview" 
              style={imagePreviewStyle}
            />
          </div>
        )}

        <button 
          onClick={handleUpload} 
          disabled={!file || loading}
          style={!file || loading ? disabledButtonStyle : buttonStyle}
          onMouseOver={(e) => {
            if (!(!file || loading)) {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 6px 20px rgba(102, 126, 234, 0.6)';
            }
          }}
          onMouseOut={(e) => {
            if (!(!file || loading)) {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 4px 15px rgba(102, 126, 234, 0.4)';
            }
          }}
        >
          {loading ? (
            <span>
              <span style={{ display: 'inline-block', animation: 'spin 1s linear infinite', marginRight: '0.5rem' }}>⏳</span>
              Analyzing Product...
            </span>
          ) : (
            <span>
              <span style={{ marginRight: '0.5rem' }}>🚀</span>
              Generate Product Description
            </span>
          )}
        </button>

        {error && (
          <div style={errorStyle}>
            <strong>❌ Error:</strong> {error}
          </div>
        )}
      </div>

      {result && (
        <div style={cardStyle}>
          <h3 style={{ color: '#2c3e50', marginBottom: '1.5rem', display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
            <span>✨</span>
            AI Generated Product Details:
          </h3>
          <div style={resultStyle}>
            <div style={{ display: 'grid', gap: '1rem' }}>
              <div>
                <strong style={{ color: '#667eea' }}>Product Name:</strong>
                <p style={{ margin: '0.5rem 0', fontSize: '1.1rem' }}>{result.product_name}</p>
              </div>

              <div>
                <strong style={{ color: '#667eea' }}>Description:</strong>
                <p style={{ margin: '0.5rem 0', lineHeight: '1.6' }}>{result.product_description}</p>
              </div>

              <div>
                <strong style={{ color: '#667eea' }}>Category:</strong>
                <p style={{ margin: '0.5rem 0' }}>{result.product_category}</p>
              </div>

              {result.highlights && result.highlights.length > 0 && (
                <div>
                  <strong style={{ color: '#667eea' }}>Highlights:</strong>
                  <ul style={{ margin: '0.5rem 0', paddingLeft: '1.5rem' }}>
                    {result.highlights.map((highlight, index) => (
                      <li key={index} style={{ marginBottom: '0.25rem' }}>{highlight}</li>
                    ))}
                  </ul>
                </div>
              )}

              {result.tags && result.tags.length > 0 && (
                <div>
                  <strong style={{ color: '#667eea' }}>Tags:</strong>
                  <div style={{ marginTop: '0.5rem', display: 'flex', flexWrap: 'wrap', gap: '0.5rem' }}>
                    {result.tags.map((tag, index) => (
                      <span 
                        key={index}
                        style={{
                          background: '#e8f5e8',
                          color: '#2d5a2d',
                          padding: '0.25rem 0.75rem',
                          borderRadius: '20px',
                          fontSize: '0.9rem',
                          border: '1px solid #c8e6c9'
                        }}
                      >
                        {tag}
                      </span>
                    ))}
                  </div>
                </div>
              )}

              {result.brand && (
                <div>
                  <strong style={{ color: '#667eea' }}>Brand:</strong>
                  <p style={{ margin: '0.5rem 0' }}>{result.brand}</p>
                </div>
              )}

              {result.holiday && (
                <div>
                  <strong style={{ color: '#667eea' }}>Holiday:</strong>
                  <p style={{ margin: '0.5rem 0' }}>{result.holiday}</p>
                </div>
              )}
            </div>

            <details style={{ marginTop: '2rem' }}>
              <summary style={{ cursor: 'pointer', color: '#667eea', fontWeight: '600' }}>
                View Raw JSON Data
              </summary>
              <pre style={{ 
                background: '#f1f3f4', 
                padding: '1rem', 
                borderRadius: '8px', 
                marginTop: '1rem',
                overflow: 'auto',
                fontSize: '0.85rem'
              }}>
                {JSON.stringify(result, null, 2)}
              </pre>
            </details>
          </div>
        </div>
      )}

      <style jsx>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
}

export default App;
